    await mfkdf.policy.or(
      await mfkdf.setup.factors.password('passwordB', {
        id: 'passwordB'
      }),
      await mfkdf.setup.factors.password('passwordC', { id: 'passwordC' })
    )
  )
)
// get list of ids
