const keyPair = await crypto.webcrypto.subtle.generateKey(
  {
    hash: 'SHA-256',
    modulusLength: 2048,
    name: 'RSA-OAEP',
    publicExponent: new Uint8Array([1, 0, 1])
  },
  true,
  ['encrypt', 'decrypt']
)
const init = await mfkdf.setup.key([
  await mfkdf.setup.factors.password('password')
])
const passwordSetup = await mfkdf.stage.factor.setup(
  mfkdf.setup.factors.password('password'),
  init.key
)
const hmacsha1Setup = await mfkdf.stage.factor.setup(
  mfkdf.setup.factors.hmacsha1(),
  init.key
)
const hotpSetup = await mfkdf.stage.factor.setup(
  mfkdf.setup.factors.hotp({ secret: Buffer.from('abcdefghijklmnopqrst') }),
  init.key
)
const oobaSetup = await mfkdf.stage.factor.setup(
  mfkdf.setup.factors.ooba({ key: keyPair.publicKey, params: {} }),
  init.key
)
const questionSetup = await mfkdf.stage.factor.setup(
  mfkdf.setup.factors.question('fido'),
  init.key
)
const totpSetup = await mfkdf.stage.factor.setup(
  mfkdf.setup.factors.totp({
    secret: Buffer.from('abcdefghijklmnopqrst'),
    time: 1
  }),
  init.key
)
const uuidSetup = await mfkdf.stage.factor.setup(
  mfkdf.setup.factors.uuid({
    uuid: '9b1deb4d-3b7d-4bad-9bdd-2b0d7b3dcb6d'
  }),
  init.key
)
const setup = await mfkdf.setup.key([
  passwordSetup,
  hmacsha1Setup,
  hotpSetup,
  oobaSetup,
  questionSetup,
  totpSetup,
  uuidSetup
])
const secret = setup.outputs.hmacsha1.secret
const challenge = Buffer.from(
  setup.policy.factors[1].params.challenge,
  'hex'
)
const response = crypto
  .createHmac('sha1', secret)
  .update(challenge)
  .digest()
const next = setup.policy.factors[3].params.next
const decrypted = await crypto.webcrypto.subtle.decrypt(
  { name: 'RSA-OAEP' },
  keyPair.privateKey,
  Buffer.from(next, 'hex')
)
const json = JSON.parse(Buffer.from(decrypted).toString())
const code = json.code
const password = await mfkdf.stage.factor.derive(
  mfkdf.derive.factors.password('password'),
  setup.policy.factors[0].params,
  setup.key
)
const hmacsha1 = await mfkdf.stage.factor.derive(
  mfkdf.derive.factors.hmacsha1(response),
  setup.policy.factors[1].params,
  setup.key
)
const hotp = await mfkdf.stage.factor.derive(
  mfkdf.derive.factors.hotp(241063),
  setup.policy.factors[2].params,
  setup.key
)
const ooba = await mfkdf.stage.factor.derive(
  mfkdf.derive.factors.ooba(code),
  setup.policy.factors[3].params,
  setup.key
)
const question = await mfkdf.stage.factor.derive(
  mfkdf.derive.factors.question('fido'),
  setup.policy.factors[4].params,
  setup.key
)
const totp = await mfkdf.stage.factor.derive(
