const factor = await mfkdf.setup.factors.password('hello')
factor.type.should.equal('password')
factor.data.toString('hex').should.equal('68656c6c6f')
const params = await factor.params()
params.should.deep.equal({})

const factor = await mfkdf.setup.factors.uuid({
  uuid: '6ec0bd7f-11c0-43da-975e-2a8ad9ebae0b'
})
factor.type.should.equal('uuid')
factor.data
  .toString('hex')
  .should.equal('6ec0bd7f11c043da975e2a8ad9ebae0b')
const params = await factor.params()
params.should.deep.equal({})

const factor = await mfkdf.setup.factors.uuid({})
factor.type.should.equal('uuid')
const output = await factor.output()
factor.data.toString('hex').should.equal(output.uuid.replaceAll('-', ''))
const params = await factor.params()
params.should.deep.equal({})

const { policy } = await mfkdf.setup.key(
  [await mfkdf.setup.factors.password('hello')],
  { id: 'hello-world' }
)
policy.$id.should.equal('hello-world')

const { policy } = await mfkdf.setup.key(
  [
    await mfkdf.setup.factors.password('hello', { id: 'password1' }),
    await mfkdf.setup.factors.password('hello', { id: 'password2' })
  ],
  { threshold: 1 }
)
policy.threshold.should.equal(1)

await mfkdf.setup
  .key([await mfkdf.setup.factors.password('hello')], {
    threshold: 'hello'
  })
  .should.be.rejectedWith(TypeError)

const { policy } = await mfkdf.setup.key([
  await mfkdf.setup.factors.password('hello')
])
const salt = Buffer.from(policy.salt, 'base64')
salt.length.should.equal(32)

const { policy } = await mfkdf.setup.key(
  [await mfkdf.setup.factors.password('hello')],
  { salt: Buffer.from('1234567812345678', 'base64') }
)
policy.salt.should.equal('1234567812345678')

